var Lint = require("../lint");
describe("<no-var-keyword>", function () {
    var NoVarKeywordRule = Lint.Test.getRule("no-var-keyword");
    var fileName = "rules/novarkeyword.test.ts";
    it("disallows use of creating variables with 'var'", function () {
        var createFailure = Lint.Test.createFailuresOnFile(fileName, NoVarKeywordRule.FAILURE_STRING);
        var expectedFailures = [
            createFailure([1, 1], [1, 4]),
            createFailure([4, 5], [4, 8]),
            createFailure([7, 1], [7, 4]),
            createFailure([10, 1], [10, 4]),
        ];
        var actualFailures = Lint.Test.applyRuleOnFile(fileName, NoVarKeywordRule);
        Lint.Test.assertFailuresEqual(actualFailures, expectedFailures);
    });
});
