var Lint = require("../lint");
describe("<switch-default>", function () {
    var SwitchDefaultRule = Lint.Test.getRule("switch-default");
    var fileName = "rules/switchdefault.test.ts";
    var failureString = SwitchDefaultRule.FAILURE_STRING;
    it("Switch default", function () {
        var failure = Lint.Test.createFailuresOnFile(fileName, failureString);
        var expectedFailures = [
            failure([2, 1], [6, 2]),
            failure([8, 1], [18, 2])
        ];
        var actualFailures = Lint.Test.applyRuleOnFile(fileName, SwitchDefaultRule);
        Lint.Test.assertFailuresEqual(actualFailures, expectedFailures);
    });
});
